#!/bin/bash

# 🚀 Railway Deployment Configuration Generator
# Creates all necessary files for Railway deployment

echo "🚂 Creating Railway deployment configuration..."

# 1. Create Railway configuration file
echo "📄 Creating railway.json..."
cat > railway.json << 'EOF'
{
  "$schema": "https://railway.app/railway.schema.json",
  "build": {
    "builder": "nixpacks"
  },
  "deploy": {
    "numReplicas": 1,
    "sleepApplication": false,
    "restartPolicyType": "ON_FAILURE"
  }
}
EOF

# 2. Create Railway service configurations
echo "⚙️ Creating service configurations..."

# Backend service configuration
mkdir -p .railway
cat > .railway/backend.json << 'EOF'
{
  "name": "legal-estate-backend",
  "build": {
    "builder": "dockerfile",
    "dockerfilePath": "Dockerfile.backend"
  },
  "deploy": {
    "numReplicas": 1,
    "healthcheckPath": "/health",
    "healthcheckTimeout": 300
  },
  "environment": {
    "NODE_ENV": "production",
    "PORT": "3000"
  }
}
EOF

# Frontend service configuration
cat > .railway/frontend.json << 'EOF'
{
  "name": "legal-estate-frontend", 
  "build": {
    "builder": "dockerfile",
    "dockerfilePath": "Dockerfile.frontend"
  },
  "deploy": {
    "numReplicas": 1
  },
  "environment": {
    "NODE_ENV": "production"
  }
}
EOF

# 3. Create optimized Dockerfile for Railway
echo "🐳 Creating Railway-optimized Dockerfiles..."

# Optimized Backend Dockerfile for Railway
cat > Dockerfile.backend.railway << 'EOF'
# Multi-stage build for Railway optimization
FROM node:18-alpine AS base
WORKDIR /app

# Dependencies stage
FROM base AS deps
COPY backend/package*.json ./
RUN npm ci --only=production

# Build stage
FROM base AS builder
COPY backend/package*.json ./
RUN npm ci
COPY backend .
RUN npm run build

# Production stage
FROM base AS runner
ENV NODE_ENV=production
ENV PORT=3000

# Create app user
RUN addgroup --system --gid 1001 nodejs
RUN adduser --system --uid 1001 legal-estate

# Copy production dependencies
COPY --from=deps --chown=legal-estate:nodejs /app/node_modules ./node_modules

# Copy built application
COPY --from=builder --chown=legal-estate:nodejs /app/dist ./dist
COPY --from=builder --chown=legal-estate:nodejs /app/prisma ./prisma
COPY --from=builder --chown=legal-estate:nodejs /app/package*.json ./

# Generate Prisma client
RUN npx prisma generate

USER legal-estate

EXPOSE 3000

# Health check
HEALTHCHECK --interval=30s --timeout=3s --start-period=5s --retries=3 \
  CMD curl -f http://localhost:3000/health || exit 1

CMD ["npm", "start"]
EOF

# Optimized Frontend Dockerfile for Railway
cat > Dockerfile.frontend.railway << 'EOF'
# Multi-stage build for Railway optimization
FROM node:18-alpine AS base
WORKDIR /app

# Dependencies stage
FROM base AS deps
COPY frontend/package*.json ./
RUN npm ci

# Build stage
FROM base AS builder
COPY frontend/package*.json ./
RUN npm ci
COPY frontend .

ARG REACT_APP_API_URL
ENV REACT_APP_API_URL=$REACT_APP_API_URL

RUN npm run build

# Production stage - using nginx for static files
FROM nginx:alpine AS runner

# Copy built app
COPY --from=builder /app/build /usr/share/nginx/html

# Create nginx config for SPA
RUN echo 'server { \
    listen 80; \
    location / { \
        root /usr/share/nginx/html; \
        index index.html index.htm; \
        try_files $uri $uri/ /index.html; \
    } \
    location /api { \
        proxy_pass $REACT_APP_API_URL; \
        proxy_set_header Host $host; \
        proxy_set_header X-Real-IP $remote_addr; \
    } \
}' > /etc/nginx/conf.d/default.conf

EXPOSE 80

CMD ["nginx", "-g", "daemon off;"]
EOF

# 4. Create Railway deployment script
echo "🚀 Creating Railway deployment script..."
cat > deploy-railway.sh << 'EOF'
#!/bin/bash

# 🚂 Legal Estate - Railway Deployment Script
set -e

# Colors
GREEN='\033[0;32m'
BLUE='\033[0;34m'
YELLOW='\033[1;33m'
RED='\033[0;31m'
NC='\033[0m'

print_step() { echo -e "${BLUE}➜ $1${NC}"; }
print_success() { echo -e "${GREEN}✅ $1${NC}"; }
print_warning() { echo -e "${YELLOW}⚠️ $1${NC}"; }
print_error() { echo -e "${RED}❌ $1${NC}"; }

# Check if Railway CLI is installed
if ! command -v railway >/dev/null 2>&1; then
    print_error "Railway CLI not found!"
    echo
    print_step "Installing Railway CLI..."
    
    if command -v npm >/dev/null 2>&1; then
        npm install -g @railway/cli
    elif command -v yarn >/dev/null 2>&1; then
        yarn global add @railway/cli
    else
        print_error "Please install Node.js/npm first"
        exit 1
    fi
    
    print_success "Railway CLI installed"
fi

# Check if logged in
if ! railway whoami >/dev/null 2>&1; then
    print_step "Please login to Railway..."
    railway login
fi

print_step "Logged in as: $(railway whoami)"

# Check if project exists
if ! railway status >/dev/null 2>&1; then
    print_step "Creating new Railway project..."
    railway new legal-estate-demo
    print_success "Project created"
else
    print_step "Using existing Railway project"
fi

# Set environment variables
print_step "Setting environment variables..."

# Backend environment variables
railway variables set NODE_ENV=production
railway variables set JWT_SECRET="${JWT_SECRET:-legal-estate-production-secret-$(openssl rand -hex 16)}"
railway variables set FRONTEND_URL="https://legal-estate-frontend.railway.app"

# Database URL (using existing Neon database)
if [[ -f "backend/.env" ]] && grep -q "DATABASE_URL" backend/.env; then
    DB_URL=$(grep "DATABASE_URL" backend/.env | cut -d'=' -f2- | tr -d '"')
    railway variables set DATABASE_URL="$DB_URL"
    print_success "Database URL configured from backend/.env"
else
    print_warning "DATABASE_URL not found in backend/.env"
    print_step "Please set it manually: railway variables set DATABASE_URL='your-neon-url'"
fi

print_success "Environment variables configured"

# Deploy backend service
print_step "Deploying backend service..."
railway up --detach

# Wait for backend deployment
print_step "Waiting for backend deployment..."
sleep 30

# Get backend URL
BACKEND_URL=$(railway domain 2>/dev/null || echo "https://legal-estate-backend.railway.app")
print_success "Backend deployed at: $BACKEND_URL"

# Deploy frontend with backend URL
print_step "Deploying frontend service..."
railway variables set REACT_APP_API_URL="$BACKEND_URL"
railway up --detach

print_success "🎉 Deployment completed!"
echo
print_step "Your Legal Estate demo is available at:"
echo "  🌐 Frontend: https://legal-estate-frontend.railway.app"
echo "  🔧 Backend: $BACKEND_URL"
echo "  📊 Railway Dashboard: https://railway.app/dashboard"
echo
print_step "Default login credentials:"
echo "  📧 Email: admin@example.com"
echo "  🔑 Password: admin123"
echo
print_warning "⚠️ Remember to change default credentials in production!"

# Show next steps
echo
print_step "Next steps:"
echo "  1. Set custom domain: railway domain add yourdomain.com"
echo "  2. Monitor logs: railway logs"
echo "  3. Scale if needed: railway scale"
echo "  4. View metrics in Railway dashboard"

# Show useful commands
echo
print_step "Useful Railway commands:"
echo "  railway logs              # View application logs"
echo "  railway logs --tail       # Follow logs in real-time"
echo "  railway shell             # SSH into container"
echo "  railway variables         # List environment variables"
echo "  railway status            # Check deployment status"
echo "  railway domain add        # Add custom domain"
EOF

chmod +x deploy-railway.sh

# 5. Create Railway monitoring script
echo "📊 Creating monitoring script..."
cat > railway-monitor.sh << 'EOF'
